mapper.update:
merge into cmt_tb c
	using (
			select max(cmt_sq) cmt_sq
			from cmt_tb
			where stf_sq = #{stf_sq}
			and cmt_end_tm is null
	      )sub
	on (
		c.cmt_sq = sub.cmt_sq
		)
	when matched then
		update
		set c.cmt_end_tm = sysdate

		, c.cmt_msg = c.cmt_msg || #{cmt_msg}

		where c.cmt_sq = sub.cmt_sq
